  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/git/BYTEPATH-blogs/node_modules/.pnpm/extract-zip@1.7.0/node_modules/extract-zip/node_modules:/mnt/d/git/BYTEPATH-blogs/node_modules/.pnpm/extract-zip@1.7.0/node_modules:/mnt/d/git/BYTEPATH-blogs/node_modules/.pnpm/node_modules:/mnt/d/git/BYTEPATH-blogs/node_modules:/mnt/d/git/node_modules:/mnt/d/node_modules:/mnt/d/git/BYTEPATH-blogs/node_modules/.pnpm/phantomjs-prebuilt@2.1.16/node_modules/phantomjs-prebuilt/node_modules:/mnt/d/git/BYTEPATH-blogs/node_modules/.pnpm/phantomjs-prebuilt@2.1.16/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
