}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../extract-zip@1.7.0/node_modules/extract-zip/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../extract-zip@1.7.0/node_modules/extract-zip/cli.js" $args
  }
  $ret=$LASTEXITCODE
