  } else {
    & "node$exe"  "$basedir/../../../../../extract-zip@1.7.0/node_modules/extract-zip/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
